import './home.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import React,  { useState } from 'react';

export default function Home() {

  //TODO:  add Texto experimento
  //TODO: agregar una fase de prueba 
  const [userIdRegistered, setuserIdRegistered] = useState(false);
  return (
    <div>
      <Header/>
      <SubHeader/>
      <GetCellPhoneFromUser registerUser={ () => setuserIdRegistered(true)}/>
      <StartExperimentButton userIdRegistered={userIdRegistered == false}/>
    </div>
  );
}

function Header(){
  return (
    <div className="Header">
      <h1> Experimento </h1>
    </div>

  );
}

function SubHeader(){
  return (
    <div >
      Explicacion del exp y condiciones para participar  
    </div>

  );
}

function StartExperimentButton({userIdRegistered}) {
  const navigate = useNavigate();

  const handleClick = () => {
    if(userIdRegistered){
      alert('Va a arrancar el experimento!!');
      navigate('/experiment')
    }else{
      alert('Registrate con tu celular antes de comenzar')
    }

  };

  return (
    <div style={{textAlign : 'center'}}>
      <button className= "buttonStyle" onClick={handleClick}>Comenzar</button> {/* Add onClick event with the function to handle button click */}
    </div>
  );
}

function GetCellPhoneFromUser({registerUser}) {

  //TODO: parsear tel para que sea valido
  const [userId, setUserId] = useState('');
  const [userExists, setUserExists] = useState(false);

  const handleInputChange = (event) => {
    setUserId(event.target.value);
    setUserExists(false);
  };


  /* const [imgId, setImgId] = useState(4);
  const [imgGroup, setImgGroup] = useState(1);
  const [imgGeneratedBy, setImgGeneratedBy] = useState('SD');
  const [promptUsed, setPromptUsed] = useState(100);
  const [rating, setRating] = useState(1); */

  const handleCheckUser = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/checkUser/${userId}`);
      const { userExists } = response.data;
      setUserExists(userExists);
    } catch (error) {
      console.error('Error checking user:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await handleCheckUser();

    if (userExists !== null) {
      console.log('User already exists');
      alert('Usuario ya registrado!')
    } else {
      console.log('Add new user: User does not exist');
      try {
        const response = await axios.post('http://localhost:5000/api/addUser', {
           userId
         });
         registerUser();
       } catch (error) {
         console.error('Error submitting data:', error);
       }
    }
  };

  return (
    <div>
      <h1>Ingresa tu telefono antes de comenzar</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={userId}
          onChange={handleInputChange}
          placeholder="Ingresa tu telefono"
        />
        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
}
