import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'
import React,  { Component, useState } from 'react';




export default class FiveStarsRating extends Component{

    constructor(props){
        super(props);
        this.state = {currRating : 0}
        this.onHover = this.onHover.bind(this)
        this.onClick = this.onClick.bind(this)
    }

    onHover(e) {
        if (e.target.className === 'star') {
            this.setRating(e.target.index)
        }
    }
    onClick(e) {
        if (e.target.className === 'star') {
            this.setRating(e.target.index)
        }
    }
    setRating(value) {
        this.setState({currRating : value})
      }

    render(){
        return(
            <section>
              {[...Array(5).keys()].map((index) => (
                  <Star index ={index} isFilled= {index < this.state.currRating} onClick={this.onClick} 
                  onMouseOver={()=> this.onHover}/>))
              }
            </section>
            )
    }

  }




const Star = ({ index, onClick, onHover, isFilled }) => {
    const [isClicked, setIsClicked] = useState(false);
    const [isHovered, setIsHovered] = useState(false);

    const handleClick = () => {
      setIsClicked(!isClicked);
      onClick && onClick();
    };
    const handleOver= () => {
        setIsHovered(!isHovered);
        onHover();
    };

    const starColor = isClicked || isFilled ? "gold" : "gray";
    return (
        <span style={{color: starColor}} className='star'> 
          <FontAwesomeIcon icon={faStar} style={ {height:'40px'}}
          onClick={handleClick} onHover = {handleOver}/>   
        </span>
    );
  };
