import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'
import React,  { Component, useState } from 'react';




export default class FiveStarsRating extends Component{

    constructor(props){
        super(props);
        this.state = {currRating : 0}
        this.onHover = this.onHover.bind(this)
        this.onClick = this.onClick.bind(this)
    }

    onHover(e) {
        if (e.target.className === 'star') {
            this.setRating(e.target.index)
        }
    }
    onClick(e) {
        if (e.target.className === 'star') {
            this.setRating(e.target.index)
        }
    }
    setRating(value) {
        this.setState({currRating : value})
    }
    starColor(index){
        return((this.state.currRating < index) ? "gold" : "gray");

    } 

    render(){
        return(
            [...Array(5).keys()].map((index) => {
                return(

                <span style={{color: starColor(index)}} className='star'> 
                  <FontAwesomeIcon icon={faStar} style={ {height:'40px'}} onClick={handleClick} onHover = {handleOver}/>   
                </span>)
                })
            )
        }
    }



