import { useState } from 'react';
import { originalImagesList } from './data';

export default function ExperimentImages() {
  const [index, setIndex] = useState(0);
  const [nextImage, setNextImage] = useState(false);

  function handleNextClick() {
    setIndex(index + 1);
  }

  function handleMoreClick() {
    setNextImage(!nextImage);
  }

  let originalImage = originalImagesList[index];
  return (
    <>
      <button onClick={handleNextClick}>
        Next
      </button>
      <h2>
        <i>{originalImage.name} </i> 
      </h2>
      <h3>  
        ({index + 1} of {originalImagesList.length})
      </h3>
      <img 
        src={originalImage.dir} 
        alt=''
      />
    </>
  );
}
