import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons'
import React,  { Component, useState } from 'react';




class FiveStarsRating extends Component{

    constructor(props){
        super(props);
        this.state = {currRating : 0}
        this.onHover = this.onHover.bind(this)
        this.onClick = this.onClick.bind(this)
    }

    onHover(e) {
        if (e.target.className === 'star') {
            this.setRating(e.target.dataset.value)
        }
    }
    onClick(e) {
        if (e.target.dataset.value === this.state.currRating){
            this.setRating(e.target.dataset.value - 1)
        }
    }
    setRating(value) {
        this.setState({currRating : value})
      }

    render(){
        return(
            <section>
              {[...Array(5).keys()].map((index) => (
                  <Star index ={index} isFilled= {index < rating} onClick={() => handleRatingClick()} />))
              }
            </section>
            )
    }

  }



const Star = ({ index, onClick, isFilled }) => {
    const [isClicked, setIsClicked] = useState(false);

    const handleClick = () => {
      setIsClicked(!isClicked);
      onClick && onClick();
    };
    const starColor = isClicked || isFilled ? "gold" : "gray";
    return (
        <span onClick={handleClick} style={{color: starColor}}> 
          <FontAwesomeIcon icon={faStar} style={ {height:'40px'}}/>   
        </span>
    );
  };
